import React from 'react';
import './MenuBar.css';

const MenuBar = () => {
    // Define a function to handle file downloads
    const handleDownload = (fileType) => {
        // Example file path - replace with your actual file paths or generated content
        const filePath = `/path-to-your-files/${fileType}.pdf`;

        // Create a temporary anchor element
        const link = document.createElement('a');
        link.href = filePath;
        link.download = `${fileType}.pdf`;

        // Append to the document, trigger the download, and then remove the element
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };


    return (
        <div className="menu-bar">
            <button className="menu-button" onClick={() => handleDownload('Master Files')}>Master Files</button>
            <button className="menu-button" onClick={() => handleDownload('Meta Data')}>Meta Data</button>
            <button className="menu-button" onClick={() => handleDownload('Contracts')}>Contracts</button>
            <button className="menu-button" onClick={() => handleDownload('Stems')}>Stems</button>
            <button className="menu-button" onClick={() => handleDownload('Deals')}>Deals</button>
        </div>
    );
};

export default MenuBar;
