import React, { useRef } from 'react';
import './MenuBar.css';

const MenuBar = () => {
    const fileInputRef = useRef(null);

    const handleUpload = () => {
        fileInputRef.current.click(); // Trigger file input click
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            // Handle the file upload logic here
            console.log('File selected:', file.name);
            alert('File uploaded successfully');
        }
    };

    const handleDownload = () => {
        // Logic for file download
        alert('Download file');
    };

    return (
        <div className="menu-bar">
            <input
                type="file"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={handleFileChange}
            />
            <button className="menu-button" onClick={handleUpload}>Upload File</button>
            <button className="menu-button" onClick={handleDownload}>Download File</button>
            <button className="menu-button">Master Files</button>
            <button className="menu-button">Meta Data</button>
            <button className="menu-button">Contracts</button>
            <button className="menu-button">Stems</button>
            <button className="menu-button">Deals</button>
        </div>
    );
};

export default MenuBar;
